--启动事件--
function CityToggleCtrl:OnCreate(obj)
    UIPage.OnCreate(self,obj)
    gameObject = obj;
    close = nil
    CityInfoCtrlBehaviour = self.gameObject:GetComponent('LuaBehaviour');
    CityInfoCtrlBehaviour:AddClick(CityTogglePanel.backBtn,self.OnBackBtn,self);
    CityInfoCtrlBehaviour:AddClick(CityTogglePanel.companyNumBtn,self.OnCompanyNumBtn,self);
    CityTogglePanel.time.text = os.date("%m月".."\n".."%d");

    tableSort(MaterialCommodityProcess,CityTogglePanel.company)   --生成cityinfoName表格

    Event.AddListener("c_OnBtn",self.c_OnBtn,self)

end
function CityToggleCtrl:Awake(go)
    self.gameObject = go
    CityToggleCtrl.CityMaterial = {}
    CityToggleCtrl.CityMaterial = CityMaterial
    CityToggleCtrl.static.luaBehaviour = self.gameObject:GetComponent('LuaBehaviour')

    self.material = UnityEngine.UI.LoopScrollDataSource.New()  --行情
    self.material.mProvideData = CityToggleCtrl.static.MaterialProvideData
    self.material.mClearData = CityToggleCtrl.static.MaterialClearData
    CityMaterialPanel.materialScroll:ActiveLoopScroll(self.material, #self.CityMaterial)

end

--返回按钮
function CityToggleCtrl:OnBackBtn()
