end

--滑动互用
CityToggleCtrl.static.MaterialProvideData = function(transform, idx)

    idx = idx + 1
    local item = CityToggleItem:new(CityToggleCtrl.CityMaterial[idx], transform,idx)
    local materialItems = {}
    materialItems[idx] = item
end
CityToggleCtrl.static.MaterialClearData = function(transform)
end

--点击参与公司人数排序
function CityToggleCtrl:OnCompanyNumBtn(go)
    table.sort( go.CityMaterial, function (m, n) return m.companyNum > n.companyNum end)
    CityTogglePanel.materialScroll:ActiveLoopScroll(go.material, #go.CityMaterial)
end

function CityToggleCtrl:c_OnBtn(go,isoppen)
