--生成预制
function CityToggleCtrl:_createCityInfoPab(path,parent)
    local prefab = UnityEngine.Resources.Load(path);
    local go = UnityEngine.GameObject.Instantiate(prefab);
    local rect = go.transform:GetComponent("RectTransform");
    go.transform:SetParent(parent.transform);
    rect.transform.localScale = Vector3.one;
    return rect
end
--打开曲线图
function CityToggleCtrl:Oppen(go)
    go.transform.sizeDelta = Vector2.New(1920,560)
    go.change:Change(go.element,560)
    go.gameObject:SetActive(true)
end
--关闭曲线图
function CityToggleCtrl:Close(go)
    go.transform.sizeDelta = Vector2.New(1920,130)
    go.change:Change(go.element,130)
    go.gameObject:SetActive(false)
end