    -- 结果节点
    self.evaResultSource = UnityEngine.UI.LoopScrollDataSource.New()
    self.evaResultSource.mProvideData = EvaPopCtrl.static.evaResultData
    self.evaResultSource.mClearData = EvaPopCtrl.static.evaResultClearData
end

