HouseChangeRentCtrl = class('HouseChangeRentCtrl',UIPanel)
UIPanel:ResgisterOpen(HouseChangeRentCtrl)

function HouseChangeRentCtrl:initialize()
    UIPanel.initialize(self, UIType.PopUp, UIMode.DoNothing, UICollider.Normal)
end

function HouseChangeRentCtrl:bundleName()
    return "Assets/CityGame/Resources/View/HouseChangeRentPanel.prefab"
end

function HouseChangeRentCtrl:OnCreate(obj )
    UIPanel.OnCreate(self, obj)
end

function HouseChangeRentCtrl:Awake(go)
    self.gameObject = go
    self:_getComponent(go)

    self.luaBehaviour = self.gameObject:GetComponent('LuaBehaviour')
    self.luaBehaviour:AddClick(self.closeBtn, self._onClickCloseBtn, self)
    self.luaBehaviour:AddClick(self.confirmBtn, self._onClickConfim, self)
end

function HouseChangeRentCtrl:Refresh()
    self:_initData()
end
---寻找组件
function HouseChangeRentCtrl:_getComponent(go)
    self.closeBtn = go.transform:Find("root/closeBtn").gameObject
    self.currentRentalText = go.transform:Find("root/currentRentalText"):GetComponent("Text")
    self.rentInput = go.transform:Find("root/rentInput"):GetComponent("InputField")
    self.confirmBtn = go.transform:Find("root/confirmBtn").gameObject
    self.effectiveDateText = go.transform:Find("root/effectiveDateText"):GetComponent("Text")
    self.suggestRect = go.transform:Find("root/suggestRect"):GetComponent("RectTransform")
    self.suggestRentalText = go.transform:Find("root/suggestRentalText"):GetComponent("Text")
end
---初始化
function HouseChangeRentCtrl:_initData()
    self.rentInput.text = self.m_data.rent
    local blackColor = "#4B4B4B"
    local rentalStr = string.format("%s<color=%s>%s</color>", getPriceString(self.m_data.rent, 24, 18), blackColor, "/D")
    self.currentRentalText.text = rentalStr

    local suggestStr = string.format("%sE<color=%s>%s</color>", getPriceString(self.m_data.suggestRent, 20, 18), blackColor, "/D")
    self.suggestRentalText.text = suggestStr

    local trueTextW = self.suggestRentalText.preferredWidth
    local pos = self.suggestRect.anchoredPosition
    self.suggestRect.anchoredPosition = Vector2.New(70 + 164 - trueTextW, pos.y)

    self.effectiveDateText.text = self.m_data.effectiveDate
end

function HouseChangeRentCtrl:_onClickConfim(ins)
    local inputValue = ins.rentInput.text
    if inputValue == "" then
        return
    end

    --向服务器发送请求，改变租金
    Event.Brocast("m_ReqHouseChangeRent", ins.m_data.buildingId, inputValue)
    ins:_onClickCloseBtn(ins)
end

function HouseChangeRentCtrl:_onClickCloseBtn(ins)
    UIPanel.ClosePage()
end