---建筑搜索二级菜单里的详情item
MapBuildsDetailItem = class('MapBuildsDetailItem', MapConfirmSearchBase)

--初始化方法
function MapBuildsDetailItem:childInit(viewRect)
    self.iconImg = self.viewRect.transform:Find("iconImg"):GetComponent("Image")
    self.selectImg = self.viewRect.transform:Find("selectImg")
    self.typeText = self.viewRect.transform:Find("Text"):GetComponent("Text")

    if self.data.promotionIndex ~= nil then
        local temp = MapBuildInfoConfig[self.data.promotionIndex]
        LoadSprite(temp.imgPath, self.iconImg, true)
        self.iconImg.color = getColorByVector3(temp.leftColorV3)
    end
end
--
function MapBuildsDetailItem:initLanguage()
    if self.data.promotionIndex == nil then
        return
    end
    self.typeText.text = GetLanguage(MapBuildInfoConfig[self.data.promotionIndex].languageId)
    --self.typeText.text = MapPromotionInfoConfig[self.data.promotionIndex].languageId
end
--设置toggleGroup
function MapBuildsDetailItem:setToggleGroup(toggle)
    toggle.group = MapPanel.promotionPageGroup
end
--
function MapBuildsDetailItem:getNameStr()
    return self.typeText.text
end