---Search the item base selected after expanding the secondary menu in the mini map search
MapConfirmSearchBase = class('MapConfirmSearchBase')

--Initialization method
function MapConfirmSearchBase:initialize(data, viewRect)
    self.viewRect = viewRect:GetComponent("RectTransform")
    self.data = data
    self.toggle = self.viewRect.transform:GetComponent("Toggle")
    self.toggle.onValueChanged:AddListener(function(isOn)
        PlayMusEff(1002)
        self:_toggle(isOn)
    end)

    self:setToggleGroup(self.toggle)
    self:childInit(viewRect)

    --Event.AddListener("c_SearchEndLoading", self._endLoading, self)  --End loading
    self:resetState()
end
--Reset state
function MapConfirmSearchBase:resetState()
    self.selectImg.transform.localScale = Vector3.zero
    self.toggle.isOn = false
    self:initLanguage()
end
--
function MapConfirmSearchBase:_toggle(isOn)
    if isOn == true then
        self.selectImg.localScale = Vector3.one
        Event.Brocast("c_MapSearchSelectDetail", self)  --Select item to close the details interface
    else
        self.selectImg.localScale = Vector3.zero
        Event.Brocast("c_MapSearchSelectDetail", self)  --Uncheck
    end
end
--
function MapConfirmSearchBase:getTypeId()
    return self.data.mapSearchType
end
--
function MapConfirmSearchBase:getItemId()
    return self.data.itemId
end
--
function MapConfirmSearchBase:setPos(pos)
    self.viewRect.anchoredPosition = pos
end

--
function MapConfirmSearchBase:childInit(viewRect) end
