MapDetailPageBase = class('MapDetailPageBase')

--Initialization method
function MapDetailPageBase:initialize(mapSearchType, viewRect)
    self.viewRect = viewRect
    self.mapSearchType = mapSearchType
    self:childInit(self.viewRect.transform)
    self:createItems()
end
--Reset state
function MapDetailPageBase:resetState()
    if self.items ~= nil then
        for i, value in pairs(self.items) do
            value:resetState()
        end
        self:initLanguage()
    end
end
--
function MapDetailPageBase:getTypeId()
    return self.mapSearchType
end
--
function MapDetailPageBase:toggleState(open)
    if open == true then
        self.viewRect.transform.localScale = Vector3.one
        self:initLanguage()
    else
        self.viewRect.transform.localScale = Vector3.zero
    end
end
