---推广搜索二级菜单里的详情item
MapPromotionDetailItem = class('MapPromotionDetailItem', MapConfirmSearchBase)

--初始化方法
function MapPromotionDetailItem:childInit(viewRect)
    self.iconImg = self.viewRect.transform:Find("iconImg"):GetComponent("Image")
    self.selectImg = self.viewRect.transform:Find("selectImg")
    self.typeText = self.viewRect.transform:Find("Text"):GetComponent("Text")

    if self.data.promotionIndex ~= nil then
        local temp = MapPromotionInfoConfig[self.data.promotionIndex]
        LoadSprite(temp.imgPath, self.iconImg, true)
    end
end
--
function MapPromotionDetailItem:initLanguage()
    if self.data.promotionIndex == nil then
        return
    end
    --self.typeText.text = GetLanguage(MapPromotionInfoConfig[self.data.promotionIndex].languageId)
    self.typeText.text = MapPromotionInfoConfig[self.data.promotionIndex].languageId
    self.data.itemId = self.data.promotionIndex
end
--设置toggleGroup
function MapPromotionDetailItem:setToggleGroup(toggle)
    toggle.group = MapPanel.promotionPageGroup
end
--
function MapPromotionDetailItem:getNameStr()
    return self.typeText.text
end