---Details item in the secondary menu of scientific research
MapTechnologyDetailItem = class('MapTechnologyDetailItem', MapConfirmSearchBase)

--Initialization method
function MapTechnologyDetailItem:childInit(viewRect)
    self.iconImg = self.viewRect.transform:Find("iconImg"):GetComponent("Image")
    self.selectImg = self.viewRect.transform:Find("selectImg")
    self.typeText = self.viewRect.transform:Find("Text"):GetComponent("Text")

    if self.data.technologyIndex ~= nil then
        local temp = MapTechnologyInfoConfig[self.data.technologyIndex]
        LoadSprite(temp.imgPath, self.iconImg, false)
    end
end
--
function MapTechnologyDetailItem:initLanguage()
    if self.data.technologyIndex == nil then
        return
    end
    self.typeText.text = GetLanguage(MapTechnologyInfoConfig[self.data.technologyIndex].languageId)
    --self.typeText.text = MapTechnologyInfoConfig[self.data.technologyIndex].languageId
end
--set up toggleGroup
function MapTechnologyDetailItem:setToggleGroup(toggle)
    toggle.group = MapPanel.technologyPageGroup
end
--
function MapTechnologyDetailItem:getNameStr()
    return self.typeText.text
end