OfflineNotificationLuckypageItem = class("OfflineNotificationLuckypageItem")

function OfflineNotificationLuckypageItem:initialize(prefab, data)
    self.prefab = prefab
    self.data = data

    local transform = prefab.transform
    transform:Find("FlightnumText"):GetComponent("Text").text = data.flightNo
    transform:Find("AirlineText"):GetComponent("Text").text = data.flightCompany
    transform:Find("FlightDateText"):GetComponent("Text").text = string.sub(data.flightDeptimeDate, 1, 10)
    transform:Find("ProfitText"):GetComponent("Text").text = data.profitOrLoss
end