ExchangeTransactionItem = class('ExchangeTransactionItem')
ExchangeTransactionItem.static.SELL_GREEN = Vector3.New(106,212,157)  --卖出颜色
ExchangeTransactionItem.static.BUY_RED = Vector3.New(247,144,144)

--初始化方法
function ExchangeTransactionItem:initialize(data, viewRect)
    self.viewRect = viewRect;
    self.data = data;

    local viewTrans = self.viewRect;
    self.numImg = viewTrans:Find("numImg"):GetComponent("Image");
    self.numIDText = viewTrans:Find("numImg/Text"):GetComponent("Text");
    self.valueText = viewTrans:Find("valueText"):GetComponent("Text");
    self.countText = viewTrans:Find("countText"):GetComponent("Text");

    self:_initData()
end

--初始化界面
function ExchangeTransactionItem:_initData()
    local data = self.data
    if data.isSell then
        self.numImg.color = getColorByVector3(ExchangeTransactionItem.static.SELL_GREEN)
    else
        self.numImg.color = getColorByVector3(ExchangeTransactionItem.static.BUY_RED)
    end
    self.numIDText.text = data.index
    self.valueText.text = "E"..getPriceString(data.id, 30, 24)
    self.countText.text = data.num
end