SocialityManager = class('SocialityManager')

function SocialityManager:initialize()
    self.m_friends = {}
    self.m_friendsInfo = {}
    self.m_chatInfo = {}
end

function SocialityManager:SetMyFriends(friendsData)
    if friendsData.id then
        self.m_friends[friendsData.id] = friendsData.b
    end
end

function SocialityManager:GetMyFriends()
    local friendsIdTemp = {}
    for i, v in pairs(self.m_friends) do
        if v ~= nil then
            table.insert(friendsIdTemp, {id = i, b = v})
        end
    end
    return friendsIdTemp
end

function SocialityManager:SetMyFriendsInfo(friendsData)
    if friendsData.id and friendsData.name then
        local temp = friendsData
        temp.b = self.m_friends[friendsData.id]
        self.m_friendsInfo[friendsData.id] = temp
    elseif friendsData.id and not friendsData.name then
        self.m_friendsInfo[friendsData.id] = nil
    end
end

function SocialityManager:GetMyFriendsInfo()
    local friendsInfoTemp = {}
    for _, v in pairs(self.m_friendsInfo) do
        if v ~= nil then
            table.insert(friendsInfoTemp, v)
        end
    end
    return friendsInfoTemp
end

function SocialityManager:SetMyChatInfo(chatData)
    table.insert(self.m_chatInfo, chatData)
end

function SocialityManager:GetMyChatInfo()
    return self.m_chatInfo
end