local class = require 'Framework/class'

BuildingItem = class('BuildingItem')

---初始化方法   数据（读配置表）
function BuildingItem:initialize(prefabData,prefab,inluabehaviour, mgr, id)
    self.prefab = prefab;
    self.prefabData = prefabData;
    self._luabehaviour = inluabehaviour
    self.manager = mgr
    self.id = id
    self.ItemList=mgr.buildItemList

    self.addBtn=self.prefab

    self._luabehaviour:AddClick(self.addBtn, self.OnClick_Add, self);

end

function BuildingItem:OnClick_Add(go)
    if (not  go.manager.transform)then
        return
    end
    self.transform:SetParent(go.manager.transform);
    self.transform.localPosition=Vector3.zero
    go.manager.transform=nil
end