RenterItem = class('RenterItem')
--Initialization method
function RenterItem:initialize(dataInfo, viewRect,id)
    self.dataInfo = dataInfo
    self.viewRect = viewRect
    self.id = id
    local viewTrans = self.viewRect

    self.head = viewTrans:Find("icon"):GetComponent("Image")  --Avatar
    self.space = viewTrans:Find("space/count"):GetComponent("Text")                 --Leased space
    self.len = viewTrans:Find("time/len"):GetComponent("Text")                    --Lease duration
    self.name = viewTrans:Find("icon/name"):GetComponent("Text")                    --Lease duration

    self.space.text = dataInfo.rentCapacity
    self.len.text = dataInfo.hourToRent

    PlayerInfoManger.GetInfos({dataInfo.renterId}, self.c_OnHead, self)
end

function RenterItem:c_OnHead(info)
    AvatarManger.GetSmallAvatar(info[1].faceId,self.head.transform,0.15)
    self.name.text = info[1].name
end
