require'View/BuildingInfo/outAdvertisementItem'

local class = require 'Framework/class'
ScienceItemCreatDeleteMgr = class('ScienceItemCreatDeleteMgr')

ScienceItemCreatDeleteMgr.materialItemPreb_Path="View/GoodsItem/ScienceKind"

ScienceItemCreatDeleteMgr.scienceItemPreb_Path="View/GoodsItem/ScienceItem"

ScienceItemCreatDeleteMgr.scienceItem2Preb_Path="View/GoodsItem/ScienceItem2"






function ScienceItemCreatDeleteMgr:initialize()
    self.materialItemList={}  self. materialInsList={}  ---science kind
    self.scienceItemList={}     self.scienceInsList={}    ---scienceItem

end
-------------------------------------------------------------------------------------------------------------
--- creat science kind material
function ScienceItemCreatDeleteMgr:creatSciencehallItem(luabehaviour,creatData)
    ---creat  preab
    local item=self:c_creatGoods(self.materialItemPreb_Path,ScienceSellHallPanel.materialscrollcontent)
    self.materialItemList[creatData.itemId]=item
    ---creat  ins
    local ins=ScienceKindItem:new(creatData,item,luabehaviour,self,creatData.itemId)
    self. materialInsList[creatData.itemId]=ins
end
--- creat science kind goods
function ScienceItemCreatDeleteMgr:creatSciencehallItem1(luabehaviour,creatData)
    ---creat  preab
    local item=self:c_creatGoods(self.materialItemPreb_Path,ScienceSellHallPanel.goodsscrollcontent)
    self.materialItemList[creatData.itemId]=item
    ---creat  ins
    local ins=ScienceKindItem:new(creatData,item,luabehaviour,self,creatData.itemId)
    self. materialInsList[creatData.itemId]=ins
end
