local class = require 'Framework/class'
require('Framework/UI/UIPage')
TalentTypeLuaItem = class('TalentTypeLuaItem')
local isSclect = nil

--初始化方法   数据（读配置表）
function TalentTypeLuaItem:initialize(prefab,inluabehaviour,name,mgr,DataInfo)
    self.prefab = prefab;
    self._luabehaviour = inluabehaviour;
    self.dataInfo = DataInfo
    self.manager = mgr
    self.name = self.prefab.transform:Find("name").gameObject:GetComponent("Text");
    self.bg =  self.prefab.transform:Find("bg").gameObject
    self.sclect =  self.prefab.transform:Find("sclect") 

    self.name.text = DataInfo.name
    self.sclect.localScale = Vector3.zero

    self._luabehaviour:AddClick(self.bg,self.OnBgkBtn,self);

end

function TalentTypeLuaItem:OnBgkBtn(go)
    if isSclect ~= nil then
        isSclect.localScale = Vector3.zero
    else
        go.manager.talentTypeLuaItem[1].sclect.localScale = Vector3.zero
    end
    go.sclect.localScale = Vector3.one
    isSclect = go.sclect
end