    self._luabehaviour:AddClick(self.deleteBtn.gameObject, self.OnDelete, self);

end
--删除
function WareHouseGoodsItem:OnDelete(go)
    go.manager:_deleteGoods(go)
end

--删除后刷新ID及刷新显示
function WareHouseGoodsItem:RefreshID(id)
    self.id = id
end
