laboratoryItem = class('laboratoryItem')

function laboratoryItem:initialize(dataInfo,prefab,luaBehaviour,isOther)
    self.dataInfo = dataInfo
    self.prefab = prefab

    --Research success rate
    self.laboratoryIntroduceBtn = prefab.transform:Find("materialIntroduceBtn")
    self.retailStoreText = prefab.transform:Find("materialIntroduceBtn/materialText"):GetComponent("Text")

    --Basic Research Success Rate
    self.basicQualityTextBtn = prefab.transform:Find("basisProduceRateText"):GetComponent("Text")

    --Research Success Rate Bonus
    self.qualityAdditionTextBtn = prefab.transform:Find("basisProduceRateText/symbol/produceBounusText"):GetComponent("Text")
    --self.buildTimeText = prefab.transform:Find("basisProduceRateText/symbol/basisProduceRateText/produceBounusText")


    self:language()  --self.scoreText.text = 100

    luaBehaviour:AddClick(self.basicQualityTextBtn.gameObject,self._clickBasicQualityTextBtn,self)
    luaBehaviour:AddClick(self.qualityAdditionTextBtn.gameObject,self._clickQualityAdditionTextBtn,self)
    --luaBehaviour:AddClick(self.qualityAdditionTextBtn.gameObject,self._clickPopolarityTextBtn,self)

    --Whether it is the building owner, if not, all buttons cannot be clicked
    if isOther ~= DataManager.GetMyOwnerID() then
        self.laboratoryIntroduceBtn:GetComponent("Button").interactable = false
        self.basicQualityTextBtn:GetComponent("Button").interactable = false
        self.qualityAdditionTextBtn:GetComponent("Button").interactable = false
    end
end
function laboratoryItem:language()
    self.retailStoreText.text = --[[GetLanguage(30070001)]]"研究速度"
    self.basicQualityTextBtn.text = --[[GetLanguage(30070002)]]"基础研究速度"
    self.qualityAdditionTextBtn.text = --[[GetLanguage(30070003)]]"研究速度加成"
    --self.popolarityTextBtn.text = "Reputation"
end
function laboratoryItem:_clickBasicQualityTextBtn(ins)
    local stringKey = 30070004
    Event.Brocast("openTipBox",stringKey,ins.basicQualityTextBtn.transform.localPosition,ins.basicQualityTextBtn.transform)
end
function laboratoryItem:_clickQualityAdditionTextBtn(ins)
    local stringKey = 30070005
    Event.Brocast("openTipBox",stringKey,ins.basicQualityTextBtn.transform.localPosition,ins.qualityAdditionTextBtn.transform)
end
function laboratoryItem:_clickPopolarityTextBtn(ins)
