ChatRecordsItem = class('ChatRecordsItem')
ChatRecordsItem.static.MYNAME_COLOR = "#6187D4"  -- 我自己名字的颜色
ChatRecordsItem.static.OTHERSNAME_COLOR = "#877F6E"  -- 我自己名字的颜色
ChatRecordsItem.static.MSG_COLOR = "#626262"  -- 聊天内容的颜色

-- 初始化
function ChatRecordsItem:initialize(isSelf, prefab, data)
    self.prefab = prefab
    local transform = prefab.transform

    self.msgText = transform:Find("Text"):GetComponent("Text")

    if isSelf then
        self.nameColor = ChatRecordsItem.static.MYNAME_COLOR
    else
        self.nameColor = ChatRecordsItem.static.OTHERSNAME_COLOR
    end
    self.msgText.text = string.format("<color=%s><b>[%s]:</b></color><color=%s>%s</color>",self.nameColor, data.name, ChatRecordsItem.static.MSG_COLOR, data.msg)

    transform.sizeDelta = Vector2.New(440, self.msgText.preferredHeight + 10)
end