    this.companyTwo = transform:Find("downTwo/cityinfoName"):GetComponent("GridSort");
    this.contentTwo = transform:Find("downTwo/scrollRoot/scroll/content").gameObject:GetComponent("VerticalLayoutGroup")
    this.materialLoopTwo = transform:Find("downTwo/scrollRoot/scroll"):GetComponent("LoopVerticalScrollRect")
