local transform;
local gameObject;

CityinfoSurveyPanel = {};
local this = CityinfoSurveyPanel;

--启动事件--
function CityinfoSurveyPanel.Awake(obj)
    gameObject = obj;
    transform = obj.transform;

    this.InitPanel();
    logWarn("Awake lua--->>"..gameObject.name);
end

--初始化面板--
function CityinfoSurveyPanel.InitPanel()
    this.backBtn = transform:Find("top/backBtn").gameObject;--返回按钮
    this.time = transform:Find("top/time/timeText").gameObject:GetComponent("Text"); -- 时间
    this.name = transform:Find("toggle/toggleTop/name"):GetComponent("GridSort");
    this.data = transform:Find("toggle/toggleTop/data"):GetComponent("GridSort");
    this.Scroll = transform:Find("down/scrollRoot/scroll"):GetComponent("ActiveLoopScrollRect")
    this.company = transform:Find("down/cityinfoName"):GetComponent("GridSort");
    this.content = transform:Find("downOne/scrollRoot/scroll/content").gameObject:GetComponent("VerticalLayoutGroup")
    this.Loop = transform:Find("downOne/scrollRoot/scroll"):GetComponent("LoopVerticalScrollRect")


end
