-- 子类继承实现自己的方法用以显示具体的内容
function EvaTitleItemOne:_showContent()
    self.nameText.text = EvaConfig[self.index].name
    self.IconTF = self.transform:Find("IconImage")
    self.IconTF.localScale = Vector3.zero
    self.IconImage = self.transform:Find("IconImage"):GetComponent("Image")

    LoadSprite(EvaTitleItemOne.static.ImagePath[self.index], self.IconImage, true)
    self:_setAddNumber()
end

-- 按钮点击事件、子类继承实现自己的方法
function EvaTitleItemOne:_onClickBtn()
    BaseEvaTitleItem._onClickBtn(self)
    EvaCtrl.static.evaCtrl:_showIntroduction(false)
    if EvaConfig[self.index].option then
        EvaCtrl.static.evaCtrl.isClickEva = true
        EvaCtrl.static.evaCtrl:ShowOptionTwo(#EvaConfig[self.index].option)
    else
        EvaPanel.propertyRootRt.offsetMax = Vector2.New(0, -30)
        EvaCtrl.static.evaCtrl.isClickEva = false
        EvaCtrl.static.evaCtrl:ShowOptionTwo(0)
        EvaCtrl.static.evaCtrl:ShowOptionThere(0)
        EvaCtrl.static.evaCtrl:CreatePropertyItem(EvaConfig[self.index].property)
    end
end

-- 设置按钮文字的颜色、子类继承实现自己的方法
function EvaTitleItemOne:_setNameTextColor(isSelect)
    self.nameText.color = getColorByVector3(isSelect and EvaTitleItemOne.static.NomalNameColor or EvaTitleItemOne.static.SelectNameColor)
    self.IconImage.color = getColorByVector3(isSelect and EvaTitleItemOne.static.NomalImageColor or EvaTitleItemOne.static.SelectNameColor)
end